package Dao;

public class question {
	private int idq;
	private int idf;
	private String libq;
	public question() {
		super();
		// TODO Auto-generated constructor stub
	}
	public int getIdq() {
		return idq;
	}
	public void setIdq(int idq) {
		this.idq = idq;
	}
	public int getIdf() {
		return idf;
	}
	public void setIdf(int idf) {
		this.idf = idf;
	}
	public String getLibq() {
		return libq;
	}
	public void setLibq(String libq) {
		this.libq = libq;
	}
	
}
